### Welcome to the laboratory work number 3!


<script>

var area = document.createElement('div');
document.body.appendChild( area );
area.style.height = "250px";
area.style.backgroundColor = "yellow";
area.style.position = "relative";
area.id = "area";


var td11 = document.createElement('input');
area.appendChild( td11 );
td11.style.position = "absolute";
td11.placeholder="ведите массу"
td11.style.left = "20px";
td11.style.top = "10px";
td11.style.width = "270px";
td11.style.height = "50px";
td11.style.fontSize = "36px";
td11.textAlign = "center";
td11.textJustify = "center";
td11.id = "td11";

var td12 = td11.cloneNode(false);
area.appendChild( td12 );
td12.placeholder="введите высоту";
td12.style.left = "20px";
td12.style.top = "70px";
td12.id = "td12";


var button = td11.cloneNode(false);
area.appendChild( button );
button.type = "button";
button.value = "Вычислить";
button.style.left = "10px";
button.style.top = "155px";
button.style.width = "200px";
button.addEventListener( "click", A );
   
var span = document.createElement('span');
area.appendChild( span );
span.style.position = "absolute";
span.style.left = "250px";
span.style.top = "155px";
span.style.width = "60px";
span.style.height = "60px";
span.style.fontSize = "40px";
span.innerHTML = "Ответ";
span.id = "answer";


function A() {
var a11, a12, opr;
a11 = parseInt( document.getElementById("td11").value );
a12 = parseInt( document.getElementById("td12").value );


opr = a11 * 9.81 * a12;;
document.getElementById("answer").innerHTML = 
String( opr );


}

</script>






